            <div class="data">
                <img class="logo" src="/images/auth/logo.png" :alt="$store.state.APP_NAME">

                <div class="intro" v-if="email_sent">
                    <h6>We have sent u an email ‚úâÔ∏è</h6>
                    <p>Password recovery link sent to: <span class="highlight">{{ this.email }}</span> Please fallow further instructions in the email to continue.</p>
                    <div class="resend">Didn't get the email? <a href="#" @click.prevent="forgotPassword">Resend</a></div>
                </div>
                <div class="intro" v-else>
                    <h6>Forgot Password? üîí</h6>
                    <p>Enter your email and we'll send you instructions to reset your password </p>
                </div>

                <div class="form" v-if="!email_sent">
                        <div :class="['input-wrapper', email ? 'has-content' : '', email_validation ? 'error' : '', emailClassFocus]">
                            <div class="label">{{ trans.email }}</div>
                            <div class="label-floating">{{ trans.email }}</div>
                            <input v-model="email" type="email" :class="email_validation ? 'error' : ''" @focus="emailFocus" @blur="emailBlur"/>
                            <span class="error" v-if="email_validation">{{ email_validation }}</span>
                        </div>

                        <div class="primary-actions">
                            <button @click="forgotPassword" :disabled="!validateForm">{{ trans.forgot_password }}</button>
                            <router-link :to="login_url">{{ trans.login }}</router-link>
                        </div>
                    </div>
            </div>
        </div>
    </div>
</template>

<script>
import KeyboardEvents from "../../../components/keyboardEvents.vue";

export default {
    components: {
        KeyboardEvents
    },
    data() {
        return {
            pageLoaded: false,

            title: 'Forgot password',

            email: '',
            emailClassFocus: false,
            email_validation: null,

            email_sent: false,

            trans: {
                email: 'Email',
                forgot_password: 'Send reset link',
                login: 'Back to login',
                message: {
                    email_required: 'Email is required',
                    email_format: 'Incorrect email format',
                    email_not_exist: 'We could not find this e-mail, please try again',
                    auth_confirm_forgot_password_confirmation_code_sent: 'Password recovery link successfully sent to your email',
                }
            }
        }
    },
    computed: {
        login_url() {
            return '/system/login';
        },
        validateForm() {
            return this.email !== '';
        },
    },
    methods: {
        KeyboardEvents(e) {
            if (e.which === 13 && this.validateForm) {
                this.forgotPassword();
            }
        },
        emailFocus() {
            this.emailClassFocus = 'focus';
        },
        emailBlur() {
            this.emailClassFocus = false;
        },
        forgotPassword() {
            this.$store.dispatch('clearMessages');
            this.email_validation = null;

            let payload = {
                email: this.email,
            };

            this.$store.dispatch('forgotPassword', payload)
                .then(() => {
                    this.email_sent = true;
                })
                .catch(res => {
                    if (res.errors) {
                        this.email_validation = res.errors.email ? this.trans.message[res.errors.email[0]] : null;
                    }
                });
        },
    },
    mounted() {
        this.title = this.trans.title + ' | ' + this.$store.state.APP_NAME;
        this.pageLoaded = true;
    },
    metaInfo() {
        return {
            title: this.title
        }
    }
}
</script>

